use std::env;
use std::fs;
use std::process;

fn main() {
    let args: Vec<String> = env::args().collect();
    
    if args.len() != 3 {
        eprintln!("Usage: {} <3-letter-key> <filename>", args[0]);
        eprintln!("Example: {} abc message.txt", args[0]);
	sample();
	process::exit(1);
    }
    
    let key_str = &args[1];
    let filename = &args[2];
    
    // Validate key is exactly 3 lowercase letters
    if key_str.len() != 3 || !key_str.chars().all(|c| c.is_ascii_lowercase()) {
        eprintln!("Error: Key must be exactly 3 lowercase letters");
        process::exit(1);
    }
    
    let key: [u8; 3] = [
        key_str.as_bytes()[0],
        key_str.as_bytes()[1],
        key_str.as_bytes()[2],
    ];
    
    // Read the file
    let contents = match fs::read(filename) {
        Ok(data) => data,
        Err(e) => {
            eprintln!("Error reading file '{}': {}", filename, e);
            process::exit(1);
        }
    };
    
    // Encrypt the data
    let encrypted = encrypt(&contents, &key);
    
    // Output as CSV
    println!("DATA:");
    println!("```");
    for (i, &byte) in encrypted.iter().enumerate() {
        if i > 0 {
            print!(",");
        }
        print!("{}", byte);
    }
    println!();
    println!("```");
    
    // Display statistics
    eprintln!("\nEncryption complete!");
    eprintln!("Key used: {}", key_str);
    eprintln!("Original file size: {} bytes", contents.len());
    eprintln!("Encrypted values: {} bytes", encrypted.len());
}

fn encrypt(data: &[u8], key: &[u8; 3]) -> Vec<u8> {
    data.iter()
        .enumerate()
        .map(|(i, &byte)| byte ^ key[i % 3])
        .collect()
}

