fn decrypt(data: &[u8], key: &[u8; 3]) -> Vec<u8> {
    data.iter()
        .enumerate()
        .map(|(i, &byte)| byte ^ key[i % 3])
        .collect()
}

fn score_text(text: &[u8]) -> i32 {
    let mut score = 0;
    for &byte in text {
        match byte {
            b' ' => score += 13,
            b'e' | b'E' => score += 12,
            b'a' | b'A' => score += 8,
            b't' | b'T' => score += 9,
            b'o' | b'O' => score += 8,
            b'i' | b'I' => score += 7,
            b'n' | b'N' => score += 7,
            b's' | b'S' => score += 6,
            b'h' | b'H' => score += 6,
            b'r' | b'R' => score += 6,
            b'A'..=b'Z' | b'a'..=b'z' => score += 3,
            b'0'..=b'9' | b'.' | b',' | b'!' | b'?' | b'\'' | b'"' | b';' | b':' => score += 1,
            _ => score -= 10,
        }
    }
    score
}